 public static void countAndAssign(Opportunity application, String stageName, Account candidate) {
 Outputs counts = CountStepsNotCompletedService.count(application, stageName, candidate);
 assign(counts, application);
 }

 public static void assign(Outputs counts, Opportunity application) {
 application.Steps_Need_Grading__c = counts.stepsNeededGrading;
 application.Steps_Not_Completed__c = counts.stepsNotCompleted;
 application.Steps_Pending_Proctor_Result__c = counts.stepsPendingProctorResult;
 application.StepsFailed__c = counts.stepsFailed;
 application.Ishql__c = counts.isHql;
 application.PassFail__c = counts.isMql;
 application.qualificationOverride = counts.qualificationOverride;
 }

 public static Map<Id, Account> getCandidatesForApplications(List<Opportunity> applications) {
 Set<Id> candidateIds = new Set<Id>();
 for (Opportunity application : applications) {
 candidateIds.add(application.AccountId);
 }
 return candidateIds;
 }
