public class LinkedInSlots_ComputeCellMetricsBatch implements Database.Batchable<sObject>, Schedulable {

 private Map<String, List<SourcingPlatform>> sourcingPlatforms;
 private Coordinator coordinator;
 private DateTime minLastModifiedDateTime;

 public LinkedInSlots_ComputeCellMetricsBatch(Coordinator coordinator, Map<String, List<SourcingPlatform>> sourcingPlatforms, DateTime minLastModifiedDateTime) {
 this.coordinator = coordinator;
 this.sourcingPlatforms = sourcingPlatforms;
 this.minLastModifiedDateTime = minLastModifiedDateTime;
 this.minLastModifiedDateTime = minLastModifiedDateTime.minusDays(1);
 }

 for (SourcingPlatformDetails platform : sourcingPlatforms) {
 processedCells.put(platform.recordTypeName, new Set<Id>());
 }
